@section('adminlte_js')
    <script src="{{ asset('libs/sweetalert2/js/sweetalert2@11.js') }}"></script>
    <script>
        let base_path = "{{ url('/') }}";
        let toast = configToast();
        function configToast(){
            return Swal.mixin({
                toast: true,
                position: 'top-end',
                showConfirmButton: false,
                timer: 3000,
                timerProgressBar: true,
                didOpen: (toast) => {
                    toast.addEventListener('mouseenter', Swal.stopTimer)
                    toast.addEventListener('mouseleave', Swal.resumeTimer)
                }
            })
        }
    </script>
    @stack('js')
    @yield('js')
@stop
